import React, { useState, useEffect, useLayoutEffect } from "react";
import AdminLayout from "../../components/AdminLayout/AdminLayout";
import { useRouter } from "../../hooks/use-router";
import { URL_PAGE } from "../../common/constants";
import axios from "../../common/axios";
import { storage } from "../../common/utils";
import "./styles.scss";
import jwt_decode from "jwt-decode";
import { toast } from "react-toastify";
import Moment from "react-moment";
import {
  BsCheckCircle,
  BsInfoCircleFill,
  BsFillHandThumbsUpFill,
  BsFillHandThumbsDownFill,
} from "react-icons/bs";

const localStorage = storage();
const SmokeTesting = React.memo(() => {
  let styleIconPass = { fontSize: "24", color: "#5AF0A9", marginRight: "5" };
  let styleIconFail = { fontSize: "24", color: "#F05E59", marginRight: "5" };
  const { history } = useRouter();
  const [role, setRole] = useState("");
  const [data, setData] = useState([]);
  const [histories, setHistories] = useState([]);
  const [isPass, setPass] = useState(false);
  const [isEdit, setEdit] = useState(false);
  const [smokeTestS, setsmokeTestS] = useState([]);
  const [isDetail, setDetail] = useState(false);
  const [isAdmin, setAdmin] = useState(false);
  const [id, setId] = useState("");
  const [url, setUrl] = useState("");
  const [note, setNote] = useState("");
  const handleOnContinue = () => {
    history.push(URL_PAGE.PROFILE);
  };

  useLayoutEffect(() => {
    const token = localStorage.getToken();
    if (token) {
      const decode = jwt_decode(token);
      if (decode.isAdmin) {
        setAdmin(decode.isAdmin);
      } else {
        axios({
          method: "GET",
          url: "http://stageapi.test-harness.io/user/getOne",
        })
          .then((res) => {
            setRole(res.data.role);
          })
          .catch((err) => {
            console.log("def", err);
          });
      }
    }
  }, []);

  let getSmokeTest = () => {
    axios({
      method: "GET",
      url: "http://stageapi.test-harness.io/smokeTest/",
    })
      .then((res) => {
        setData(res.data.data);
        if (res.data.data.length > 0) getSmokeTestStatus(res.data.data[0]._id);
      })
      .catch((err) => {
        console.log("def", err);
      });
  };

  let getSmokeTestStatus = (id) => {
    const data = {
      smokeTestId: id,
    };
    axios({
      method: "POST",
      url: "http://stageapi.test-harness.io/smokeTestStatus/history",
      data,
    })
      .then((res) => {
        setHistories(res.data.data);
      })
      .catch((err) => {
        console.log("def", err);
      });
  };

  let getSmokeTestStatusAdmin = () => {
    axios({
      method: "GET",
      url: "http://stageapi.test-harness.io/smokeTest/all",
    })
      .then((res) => {
        setsmokeTestS(res.data.data);
      })
      .catch((err) => {
        console.log("def", err);
      });
  };

  let onDetail = (id) => {
    const data = {
      smokeTestId: id,
    };
    return new Promise((resolve, reject) => {
      axios
        .post("http://stageapi.test-harness.io/smokeTestStatus/history", data)
        .then((res) => resolve(res))
        .catch((err) => {
          reject(err);
        });
    });
  };

  let handleDetail = (id) => {
    const res = onDetail(id);
    setDetail(true);
    res
      .then((rs) => {
        const { data } = rs;
        return data;
      })
      .then((data) => {
        const { status } = data;
        if (status == "success") {
          setHistories(data.data);
        }
      })
      .catch((error) => {
        if (error.response.status == 400) {
          const { message } = error.response.data;
          toast.error(message, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: false,
            draggable: true,
            progress: undefined,
          });
        }
      });
  };

  let onSendTestSmoke = async (id, status, note) => {
    const data = {
      id,
      status,
      note,
    };
    return new Promise((resolve, reject) => {
      axios
        .post("http://stageapi.test-harness.io/smokeTestStatus/create", data)
        .then((res) => resolve(res))
        .catch((err) => {
          reject(err);
        });
    });
  };

  let openModal = (id) => {
    setPass(true);
    setId(id);
  };

  let close = () => {
    setPass(false);
  };

  let handleTest = (id, status, note) => {
    const res = onSendTestSmoke(id, status, note);
    res
      .then((rs) => {
        const { data } = rs;
        return data;
      })
      .then((data) => {
        const { status } = data;
        if (status == "success") {
          toast.success("Send Smoke Test Success!", {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: false,
            draggable: true,
            progress: undefined,
          });
